    private void butttonbackspaceActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_butttonbackspaceActionPerformed
        String butttonbackspace = null;
        if (textField.getText().length() > 0) {
            StringBuilder str = new StringBuilder(textField.getText());
            str.deleteCharAt(textField.getText().length() - 1);
            butttonbackspace = str.toString();
            textField.setText(butttonbackspace);

        }
    }//GEN-LAST:event_butttonbackspaceActionPerformed

