    private void butttonequalActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_butttonequalActionPerformed
        String answer;
        _2nd = Double.parseDouble(textField.getText());
        if (operation == "+") {
            result = _1st + _2nd;
            answer = String.format("%.0f", result);
            textField.setText(answer);
        } else if (operation == "-") {
            result = _1st - _2nd;
            answer = String.format("%.0f", result);
            textField.setText(answer);
        } else if (operation == "*") {
            result = _1st * _2nd;
            answer = String.format("%.0f", result);
            textField.setText(answer);
        } else if (operation == "/") {
            result = _1st / _2nd;
            answer = String.format("%.2f", result);
            textField.setText(answer);
        } else if (operation == "%") {
            result = _1st % _2nd;
            answer = String.format("%.2f", result);
            textField.setText(answer);
        }
    }//GEN-LAST:event_butttonequalActionPerformed

